import React, { useState, useRef } from 'react';
import Canvas from './Components/Canvas';
import BrushControls from './Components/BrushControls';
import './App.css';

function App() {
  // State for brush size and color
  const [brushSize, setBrushSize] = useState(5); // Default brush size
  const [brushColor, setBrushColor] = useState('#000000'); // Default brush color
  const canvasRef = useRef(); // Ref to the Canvas component


  // Handlers to update state
  const handleBrushSizeChange = (newSize) => {
    setBrushSize(newSize);
  };

  const resetCanvas = () => {
    canvasRef.current.clearCanvas(); // Call the clearCanvas method in the Canvas component
  };

  const handleBrushColorChange = (newColor) => {
    setBrushColor(newColor);
  };

  return (
    <div className="App">
      <h1>Kaleidoscope Painter</h1>
      <BrushControls
        onColorChange={handleBrushColorChange}
        onSizeChange={handleBrushSizeChange}
      />
      <button onClick={resetCanvas} style={{ margin: '10px' }}>Restart</button>
      <Canvas
          ref={canvasRef}
          width={window.innerWidth}
        height={window.innerHeight}
        brushSize={brushSize}
        brushColor={brushColor}
      />
    </div>
  );
}

export default App;
