// BrushControls.js
import React, { useState } from 'react';

const BrushControls = ({ onColorChange, onSizeChange }) => {
  const [brushSize, setBrushSize] = useState(5); // Default brush size

  // Function to handle mouse movement over the designated area
  // Could be adapted to the specific element you want to use for color changing
  const handleMouseMove = (e) => {
    const { clientX, clientY, target } = e;
    const rect = target.getBoundingClientRect();
    const x = clientX - rect.left; // x position within the element.
    const y = clientY - rect.top;  // y position within the element.

    // Simple color calculation, can be adjusted based on your needs
    const r = Math.min(255, Math.floor((x / rect.width) * 255));
    const g = Math.min(255, Math.floor((y / rect.height) * 255));
    const b = 150; // Keeping blue constant for a gradient effect
    const newColor = `rgb(${r},${g},${b})`;
    onColorChange(newColor);
  };

  return (
    <div onMouseMove={handleMouseMove} style={{ padding: '20px', border: '1px solid #ccc', marginBottom: '10px' }}>
      <div>Move your mouse over this box to change the brush color!</div>
      <div>
        <label htmlFor="brushSize">Brush Size: </label>
        <input
          id="brushSize"
          type="range"
          min="1"
          max="50"
          value={brushSize}
          onChange={(e) => {
            const newSize = e.target.value;
            setBrushSize(newSize);
            onSizeChange(newSize);
          }}
        />
      </div>
    </div>
  );
};

export default BrushControls;
