import React, { useState, useRef } from 'react';
import Canvas from './Components/Canvas';
import BrushControls from './Components/BrushControls';
import './App.css';

function App() {
  // State for brush size, brush color, and canvas background color
  const [brushSize, setBrushSize] = useState(5);
  const [brushColor, setBrushColor] = useState('#000000');
  const [backgroundColor, setBackgroundColor] = useState('pink');
  const canvasRef = useRef();

  const colorOptions = [
    { name: "Pink", color: "#ffc0cb" },
    { name: "Lavender", color: "#e6e6fa" },
    { name: "Honeydew", color: "#f0fff0" },
    { name: "Powder Blue", color: "#b0e0e6" },
    { name: "Peach", color: "#ffe5b4" },
    { name: "Misty Rose", color: "#ffe4e1" },
    { name: "Mint Cream", color: "#f5fffa" },
    { name: "Azure", color: "#f0ffff" },
    { name: "Alice Blue", color: "#f0f8ff" },
    { name: "Beige", color: "#f5f5dc" },
    { name: "Lavender Blush", color: "#fff0f5" },
    { name: "Seashell", color: "#fff5ee" },
  ];

  const handleBrushSizeChange = (newSize) => {
    setBrushSize(newSize);
  };

  const handleBrushColorChange = (newColor) => {
    setBrushColor(newColor);
  };

  const resetCanvas = () => {
    canvasRef.current.clearCanvas();
  };

  return (
    <div className="App">
      <h1 style={{ fontFamily: 'monospace' }}>Kaleidoscope Painter</h1>

      {/* Color options for background */}
      <div style={{ margin: '20px 0', display: 'flex', flexWrap: 'wrap', justifyContent: 'center' }}>
        {colorOptions.map(option => (
          <div key={option.name} style={{ margin: '10px', textAlign: 'center', fontFamily: 'monospace'  }} onClick={() => setBackgroundColor(option.color)}>
            <div style={{ width: '20px', height: '20px', marginBottom: '5px', backgroundColor: option.color, cursor: 'pointer' }}></div>
            {option.name}
          </div>
        ))}
      </div>

      <BrushControls
        onColorChange={handleBrushColorChange}
        onSizeChange={handleBrushSizeChange}
      />
      <div style={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        position: 'fixed',
        left: '0',
        right: '0',
        bottom: '20px'
      }}>
        <button onClick={resetCanvas} style={{
          padding: '10px 20px',
          fontSize: '16px',
          color: '#FFF',
          backgroundColor: 'pink',
          border: 'none',
          borderRadius: '5px',
          cursor: 'pointer',
          boxShadow: '0 2px 4px rgba(0,0,0,0.2)',
          transition: 'background-color 0.9s ease'
        }}>
          Restart
        </button>
      </div>
      <Canvas
        ref={canvasRef}
        width={window.innerWidth}
        height={window.innerHeight}
        brushSize={brushSize}
        brushColor={brushColor}
        backgroundColor={backgroundColor} // Pass the background color state to the Canvas component
      />
    </div>
  );
}

export default App;
